$(document).ready(function(){"use strict";$("a").smoothScroll(),$(".progress-bar").each(function(a,b){var c=$(b).attr("aria-valuenow"),d=$("<canvas></canvas>");$(b).parent().replaceWith(d);{var e=[{value:c,color:"#F7464A",highlight:"#FF5A5E",label:"Red"}],f=[{segmentShowStroke:!0,segmentStrokeColor:"#fff",segmentStrokeWidth:2,percentageInnerCutout:50,animationSteps:100,animationEasing:"easeOutBounce",animateRotate:!0,animateScale:!1,legendTemplate:'<ul class="<%=name.toLowerCase()%>-legend"><% for (var i=0; i<segments.length; i++){%><li><span style="background-color:<%=segments[i].fillColor%>"></span><%if(segments[i].label){%><%=segments[i].label%><%}%></li><%}%></ul>'}],d=document.createElement("canvas"),g=d.getContext("2d");new Chart(g[1]).Doughnut(e,f)}})});